// src/Login.js
import React, { useState } from 'react';
import './Login.css';
import { useHistory } from 'react-router-dom'; // Uvezi useHistory za navigaciju

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory(); // Inicijalizuj useHistory za navigaciju

    const handleLogin = async (e) => {
        e.preventDefault(); // Spreči podrazumevano ponašanje forme

        try {
            const response = await fetch('/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            if (response.ok) {
                // Ako je prijava uspešna, preusmeri na dashboard
                history.push('/dashboard'); // Navigiraj na dashboard
            } else {
                // Ako prijava nije uspešna, obavesti korisnika
                const errorData = await response.json();
                alert(errorData.message || 'Invalid username or password');
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('An error occurred. Please try again later.');
        }
    };

    return (
        <div className="login-container">
            <h1 className="title">Jolly Bringer Login</h1>
            <form onSubmit={handleLogin} className="login-form">
                <input 
                    type="text" 
                    placeholder="Username" 
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required 
                />
                <input 
                    type="password" 
                    placeholder="Password" 
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required 
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
